import React, { useState} from 'react';

const AddBox = ({lng}) =>{
   // console.log(lng);
    const [val, setValue] = useState(lng);
    const style = {
        position:'absolute',
        width:'200px',
        height:'150px',
        margin:'50px 50px',
        backgroundColor:'white',
        borderRadius:'3px',
        textAlign:'center',
        paddingTop:'30px'
    }
    const handleChange = (e) =>{
        setValue(e.target.value)
    }
    return (
        <div style= {style}>
            <input type="text" placeholder="enter something" width="100%" value={lng} onChange={(e)=>handleChange(e)} />
            <input type="text" placeholder="enter something else" width="220px"/>
            <button type="submit" style={{padding:'5px 50px',marginTop:'20px'}} >submit it</button>

        </div>
    )
}
export default AddBox;
