import React, { useState} from 'react';
import img  from '../images/refresh.png'

const AddBox = ({reload,logged,name}) =>{
   // console.log(lng);
    const [title, setTitle] = useState("");
    const [latitude, setlatitude] = useState("");
    const [longitude, setlongitude] = useState("");
    const [comments, setcomments] = useState("");
    const [author, setauthor] = useState("");
    const [visitDate, setvisitDate] = useState("");
    const [image, setimage] = useState("");
    const [loading, setLoad] = useState(false)

    const style = {
        position:'absolute',
        width:'330px',
        height:'370px',
        margin:'50px 50px',
        backgroundColor:'#FFF',
        boxShadow:'0 1px 2px rgba(0,0,0,.1)',
        borderRadius:'3px',
        textAlign:'center',
        paddingTop:'5px'
    }
    const handleChange = (e) =>{
        //setValue(e.target.value)
    }
    const handleAdd = () =>{
        setLoad(true)
        fetch('http://localhost:3000/addlogs',{
            method: 'post',
            headers:{
                'Content-Type': 'application/json'
            },
            body:JSON.stringify({
                "id":new Date().getTime(),
                "title":title,
                "latitude":latitude,
                "longitude":longitude,
                "comments" :comments,
                "author":name,
                "visitDate":visitDate,
                "image":image
             })
        })
        .then(res => res.json())
        .then(result =>{
            if(result.done){
                console.log("successfully");
                reload();
                setLoad(false)
            }else{
                alert("An error occured!")
            }
        })
    }
    return (
       logged ? <div style= {style}>
            <h3>Dedicate your experience to people</h3>
            <input type="text" placeholder="enter title" style={{
                                width:'90%',
                                height:'25px',
                                marginBottom:'10px'
                            }} onChange ={(e)=>setTitle(e.target.value)}  />
            <input type="text" placeholder="enter latitude" style={{
                                width:'40%',
                                height:'25px',
                                marginBottom:'10px',
                                marginRight:'20px'
                            }} onChange ={(e)=>setlatitude(e.target.value)} />
            <input type="text" placeholder="enter longtiude" style={{
                                width:'40%',
                                height:'25px',
                                marginBottom:'10px'
                            }} onChange ={(e)=>setlongitude(e.target.value) }/>
            <input type="text" placeholder="enter comments" style={{
                                width:'90%',
                                height:'25px',
                                marginBottom:'10px'
                            }} onChange ={(e)=>setcomments(e.target.value) }/>
            {/* <input type="text" placeholder="enter author" style={{
                                width:'90%',
                                height:'25px',
                                marginBottom:'10px'
                            }} onChange ={(e)=>setauthor(e.target.value)} 
                            value={name}/> */}
            <input type="text" placeholder="enter visitdate" style={{
                                width:'45%',
                                height:'25px',
                                marginBottom:'10px',
                                transform:'translate(-45%,0)'
                            }} onChange ={(e)=>setvisitDate(e.target.value)} />
            <input type="text" placeholder="enter image" style={{
                                width:'90%',
                                height:'25px',
                                marginBottom:'10px'
                            }} onChange ={(e)=>setimage(e.target.value) }/>

            { !loading ?
                <button type="submit"
                    style={{
                        padding: '5px 50px',
                        marginTop: '5px',
                        backgroundColor: 'white',
                        border: '1px solid black',
                        borderRadius: '2px'
                    }}
                    onClick={() => handleAdd()}

                >Mark it</button> 
                : 
                <button
                    style={{
                        padding: '5px 50px',
                        marginTop: '5px',
                        backgroundColor: 'white',
                        border: '1px solid black',
                        borderRadius: '2px',
                        color:'#4267b2'

                    }}

                >
                    <img className="loader" src={img} width={25} height={25} />
                </button> 
            }

        </div> : 
        <div style={{
                position: 'absolute',
                width: '330px',
                padding: '5px 10px',
                // height:'25px',
                lineHeight: '25px',
                borderRadius: '3px',
                margin: '50px 50px',
                backgroundColor: '#FFF',
                boxShadow: '0 1px 2px rgba(0,0,0,.1)',
                borderRadius: '3px',
                textAlign: 'center',
                paddingTop: '5px'
        }}>
        < a style={{textDecoration:'none',color:'#4267b2'}}
          href='http://localhost:3000/auth/facebook'
        >Login to share your amazing places</a>
      </div>
    )
}
export default AddBox;
