import React, { Component } from 'react';

const ImageView  = ({src,offShow,value}) =>{
    const styleDiv = {
        width:'100%',
        height:'100%',
        zIndex:'100',
        backgroundColor:'black',
        opacity:'0.7'
    }
    return (
        <div style={styleDiv}>
           <div style={{
               width:'300px',
               height:'250px',
               marginLeft:'50%',
               transform:'translate(-50%,0)',
               opacity:'1'
           }}>
                <button onClick={()=>offShow(false)}>{value }</button>
                <img src={src} />
           </div>
        </div>
    )
}

export default ImageView;
