import React, { Component } from 'react';

const ImageView  = ({src,offShow,log}) =>{
    const styleDiv = {
        width:'100%',
        height:'100%',
        zIndex:'100',
        backgroundColor: 'rgb(0,0,0)', /* Fallback color */
        backgroundColor: 'rgba(0,0,0,0.4)', /* Black w/ opacity */
        position:'relative'
    }
    return (
        <div style={styleDiv} onClick={()=>offShow(false)}>
           <div style={{
               width:'400px',
               height:'370px',
               position:'absolute',
               marginLeft:'30%',
               marginTop:'100px',
               transform:'translate(-50%,0)',
               opacity:'1'
           }}>
                <img src={src} width={700} height={500} onClick={()=>offShow(false)} />
           </div>
           <div
            style={{
                width:'350px',
                height:'500px',
                position:'absolute',
                marginLeft:'65%',
                marginTop:'100px',
                // transform:'translate(-50%,0)',
                opacity:'1',
                borderRadius : '4px',
                backgroundColor:'#FFF',
                boxShadow:'0 1px 2px rgba(0,0,0,.1)',
                padding:'15px 20px'
            }}>
                <h3>{log.title}</h3>
           </div>
        </div>
    )
}

export default ImageView;
