                style={{

                  cursor:'pointer'
                }}
              >
                <svg
                  className="marker white"
                  style={{
                    height: `${3 * viewport.zoom}px`,
                    width: `${3 * viewport.zoom}px`,
                    color:'white'
                  }}
                  version="1.1" id="Layer_1" x="0px" y="0px" viewBox="0 0 512 512">
                  <g>
                    <g>
                    <path style={{fill:'#FD003A'}} d="M256,0C156.698,0,76,80.7,76,180c0,33.6,9.302,66.301,27.001,94.501l140.797,230.414
                      c2.402,3.9,6.002,6.301,10.203,6.901c5.698,0.899,12.001-1.5,15.3-7.2l141.2-232.516C427.299,244.501,436,212.401,436,180
                      C436,80.7,355.302,0,256,0z M256,270c-50.398,0-90-40.8-90-90c0-49.501,40.499-90,90-90s90,40.499,90,90
                      C346,228.9,306.999,270,256,270z"/>
                    <path style={{fill:'#E50027'}} d="M256,0v90c49.501,0,90,40.499,90,90c0,48.9-39.001,90-90,90v241.991
                      c5.119,0.119,10.383-2.335,13.3-7.375L410.5,272.1c16.799-27.599,25.5-59.699,25.5-92.1C436,80.7,355.302,0,256,0z"/>
                    </g>
                  </g>
                </svg>
              </div>
            </Marker>
            {
              showPopup == entry.id ? (
                <Popup

                  latitude={entry.latitude}
                  longitude={entry.longitude}
                  closeButton={true}
                  closeOnClick={false}
                  dynamicPosition={true}
                  onClose={() => setShowPopup({})}
                  anchor="top" >
                  <div className="popup" style={{width:'210px',fontSize:'12px'}}>
                    <h3>{entry.title}</h3>
                    <p>{entry.comments}</p>
                    <p>Author - {entry.author}</p>
                    <p>Visited on: {new Date(entry.visitDate).toLocaleDateString()}</p>
                    {
                      entry.image && 
                      <p onClick={()=>setShow(entry.id)}><img 
                        src={entry.image} 
                        alt={entry.title} 
                        width='200px' height='90px' />
                      </p>
                    }
                  </div>
                </Popup>
              ) : null
            }
            {
              isShown == entry.id ? (
              <ImageView src={entry.image} value={isShown} offShow={(val) => setShow(val)} />
              ) : null
            }
          </React.Fragment>
        ))
      }
      {/* {
        addEntryLocation ? (
          <>
          <Marker
            latitude={addEntryLocation.latitude}
            longitude={addEntryLocation.longitude}
          >
            <div>
