import React, { useState, useEffect } from 'react';
import ReactMapGL, { Marker, Popup } from 'react-map-gl';
import ImageView from './components/ImageView';
import AddBox from './components/AddBox'



const Mapbox = () => {
  const [logEntries, setLogEntries] = useState([]);
  const [showPopup, setShowPopup] = useState({});
  const [isShown , setShow] = useState(0)
  const [isAdd, setAdd] = useState(false)
  const [point, setPoint] = useState(false)
  const [pos, setPos] = useState({})

  // const [addEntryLocation, setAddEntryLocation] = useState(null);
  const [curPos, setCur] = useState({})
  const [viewport, setViewport] = useState({
    width: '100vw',
    height: '100vh',
    latitude: 10.7781128,
    longitude: 106.65325,
    zoom: 12
  });


  const getEntries = async () => {
    const response = await fetch('http://localhost:1337/api/logs')
    const logEntries = await response.json()
    console.log(logEntries);
    // const logEntries = [
    //   {
    //     id:1,
    //     latitude:10.7781128,
    //     longitude:106.6532558,
    //     title: 'Bach Khoa University',
    //     comments: 'This is the best university of Vietnam',
    //     visitDate: '2020-06-19',
    //     author:'nguyenvanhai',
    //     image: 'https://wikitintuc.com/wp-content/uploads/2018/10/bachkhoa500-1540897389-2535-1540897584_500x300.jpg'
    //   },
    //   {
    //     id:2,
    //     latitude:10.8478,
    //     longitude:106.7839,
    //     title: 'Post and Telecommunications Institute of Technology',
    //     comments: 'This is the best university of Vietnam',
    //     visitDate: '2020-06-19',
    //     author:'trandinhtam',
    //     image:'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTf4jwGXCj45_mlRIyhJxtmGrBko2mMd8IDhQ&usqp=CAU'
    //   }
    // ]
    setLogEntries(logEntries);
  };

  const getUserPosition = async() =>{
    var getPosition = function (options) {
      return new Promise(function (resolve, reject) {
        navigator.geolocation.getCurrentPosition(resolve, reject, options);
      });
    }
    const position = await getPosition();
    const logEntries = [
      {
        id:1,
        latitude:position.coords.latitude,
        longitude:position.coords.longitude,
        title: 'Bach Khoa University',
        comments: 'This is the best university of Vietnam',
        image: ' http://localhost:5000/img2.png'
      }
    ]
    setLogEntries(logEntries);
    console.log(logEntries);
  }


  useEffect( () => {
   getEntries()
   console.log(isShown);
  }, []);

  const onClick = e => {
    //for(let i in e.lngLat) console.log(i);
    console.log('A click event has occurred at ' + e.lngLat[0]);
  setPos({lng:e.lngLat[0],lat:e.lngLat[1]})
  console.log(isAdd);
   if(isAdd) setPoint(true)

  };

  const PlusIcon = ({handleClick,value}) =>{
    const divStyle  = {
      position:'absolute',
      margin:'10px 15px',
      zIndex:'500',

    }
    const divStyle1  = {
      position:'absolute',
      margin:'10px 15px',
      zIndex:'500',
      animation:'App-logo-spin 0s',
      animationFillMode: 'forwards',


    }
    return (
      <div style={value ? divStyle1 : divStyle} onClick={()=>handleClick(!value)}> 
        <svg
                  className="marker white"
                  style={{
                    height: `${3 * viewport.zoom}px`,
                    width: `${3 * viewport.zoom}px`,
                  }}
                  version="1.1" id="Layer_1" x="0px" y="0px" viewBox="0 0 512 512">
                  <g>
                    <g>
                      <path style={{fill:'white'}} d="m272 184c-4.417969 0-8-3.582031-8-8v-176h-80v176c0 4.417969-3.582031 8-8 8h-176v80h176c4.417969 0 8 3.582031 8 8v176h80v-176c0-4.417969 3.582031-8 8-8h176v-80zm0 0"/>
                    </g>
                  </g>
                </svg>
      </div>
    )
  }
  const Point = ({pos}) =>{
    return (
      <div style={{
        position:'absolute',
        padding:'2px 7px',
        height:'15px',
        lineHeight:'15px',
        borderRadius:'3px',
        backgroundColor:'white',
        color:'black',
        top:'2%',
        left:'5%',

      }}>
       {pos.lng.toFixed(2)} - {pos.lat.toFixed(2)}
      </div>
    )
  }
  // const showAddMarkerPopup = (event) => {
  //   const [ longitude, latitude ] = event.lngLat;
  //   setAddEntryLocation({
  //     latitude,
  //     longitude,
  //   });
  // };

  return (
    <ReactMapGL
      {...viewport}
      mapStyle="mapbox://styles/thecjreynolds/ck117fnjy0ff61cnsclwimyay"
      mapboxApiAccessToken={'pk.eyJ1IjoiemF5dHNldjUiLCJhIjoiY2tjaWk4cnc2MGtiMDJxazJwMGllOThlNyJ9.HciILC1tn3zdsXCuvzdRog'}
      onViewportChange={setViewport}
      onClick={(e) => onClick(e)}

     // onDblClick={showAddMarkerPopup}
    >
      <>
        <PlusIcon handleClick={(val) => setAdd(val)} value={isAdd} />
        {
          isAdd && <AddBox  />
        }

      </>
      <>
        {
          point && <Point pos={pos} />
        }
      </>
      {
        logEntries.map(entry => (
          <React.Fragment key={entry.id}>
            <Marker
              latitude={entry.latitude}
              longitude={entry.longitude}
            >
              <div
                // onClick = {() =>{
                //   console.log("You have clicked...");
                // }}
                onClick={() => setShowPopup(entry.id)}
                style={{

                  cursor:'pointer'
                }}
              >
                <svg
                  className="marker white"
                  style={{
                    height: `${3 * viewport.zoom}px`,
                    width: `${3 * viewport.zoom}px`,
                    cursor: 'pointer'
                  }}
                  version="1.1" id="Layer_1" x="0px" y="0px" viewBox="0 0 512 512">
                  <g>
                    <g>
                    <path style={{fill:'white'}} d="M184.08,0c-74.992,0-136,61.008-136,136c0,24.688,11.072,51.24,11.536,52.36c3.576,8.488,10.632,21.672,15.72,29.4
                        l93.248,141.288c3.816,5.792,9.464,9.112,15.496,9.112s11.68-3.32,15.496-9.104l93.256-141.296
                        c5.096-7.728,12.144-20.912,15.72-29.4c0.464-1.112,11.528-27.664,11.528-52.36C320.08,61.008,259.072,0,184.08,0z
                        M293.8,182.152c-3.192,7.608-9.76,19.872-14.328,26.8l-93.256,141.296c-1.84,2.792-2.424,2.792-4.264,0L88.696,208.952
                        c-4.568-6.928-11.136-19.2-14.328-26.808C74.232,181.816,64.08,157.376,64.08,136c0-66.168,53.832-120,120-120
                        c66.168,0,120,53.832,120,120C304.08,157.408,293.904,181.912,293.8,182.152z"/>
                    <path style={{fill:'white'}} d="M184.08,64.008c-39.704,0-72,32.304-72,72c0,39.696,32.296,72,72,72c39.704,0,72-32.304,72-72
                        C256.08,96.312,223.784,64.008,184.08,64.008z M184.08,192.008c-30.872,0-56-25.12-56-56s25.128-56,56-56s56,25.12,56,56
                        S214.952,192.008,184.08,192.008z"/>
                    </g>
                  </g>
                </svg>
              </div>
            </Marker>
            {
              showPopup == entry.id ? (
                <Popup

                  latitude={entry.latitude}
                  longitude={entry.longitude}
                  closeButton={true}
                  closeOnClick={false}
                  dynamicPosition={true}
                  onClose={() => setShowPopup({})}
                  anchor="top" >
                  <div className="popup" style={{width:'210px',fontSize:'12px',zIndex:'99',position:'relative'}}>
                    <h3>{entry.title}</h3>
                    <p>{entry.comments}</p>
                    <p>Author - {entry.author}</p>
                    <p>Visited on: {new Date(entry.visitDate).toLocaleDateString()}</p>
                    {
                      entry.image && 
                      <p onClick={()=>setShow(entry.id)}><img 
                        src={entry.image} 
                        alt={entry.title} 
                        width='200px' height='90px' 
                        title="Zoom in"
                        />
                      </p>
                    }
                    <p
                      style={{
                        position:'absolute',
                        padding:'2px 7px',
                        height:'15px',
                        lineHeight:'15px',
                        borderRadius:'3px',
                        backgroundColor:'white',
                        color:'black',
                        bottom:'0%',
                        left:'2%',

                      }}
                    >Click image to view full screen</p>
                  </div>
                </Popup>
              ) : null
            }
            {
              isShown == entry.id ? (
              <ImageView src={entry.image} value={isShown} offShow={(val) => setShow(val)} />
              ) : null
            }
          </React.Fragment>
        ))
      }
      {/* {
        addEntryLocation ? (
          <>
          <Marker
            latitude={addEntryLocation.latitude}
            longitude={addEntryLocation.longitude}
          >
            <div>
              <svg
                className="marker red"
