import {USER_LOGIN, USER_LOGOUT} from './types';

export const userLogin = () => dispatch => {
    fetch('http://localhost:3000/auth/login/success')
        .then(res => res.json())
        .then(data =>
            dispatch({
                type: USER_LOGIN,
                payload: data.user
            })
        );
};

export const userLogout = () => dispatch => {
    fetch('http://localhost:3000/logout')
        .then(res => res.json())
        .then(data =>
            dispatch({
                type: USER_LOGOUT,
                payload: data.user
            })
        );
};
