import React, { Component, useState, useEffect } from 'react';
import 'antd/dist/antd.css'
import { Menu, Dropdown } from 'antd';

const User = ({ userName, url }) => {
    const [hover, setHover] = useState(false)
    const handleHover = () => {
        setHover(!hover)
    }
    const hoverContent = (
        <Menu>
            <Menu.Item>
                <a target="_blank" rel="noopener noreferrer" href="http://localhost:3000/logout">
                    Log out
                </a>
            </Menu.Item>
            <Menu.Item>
                <a target="_blank" rel="noopener noreferrer" href="http://localhost:3000/logout">
                    Custom
                </a>
            </Menu.Item>
        </Menu>

    )
    return (
        <div>
            <Dropdown overlay={hoverContent}>
                <a className="ant-dropdown-link" onClick={e => e.preventDefault()}>

                    <div className="user-image"
                        style={{
                            position: 'absolute',
                            padding: '5px 10px',
                            height: '35px',
                            lineHeight: '35px',
                            borderRadius: '3px',
                            backgroundColor: 'white',
                            color: 'black',
                            bottom: '5%',
                            left: '5%',
                            display: 'grid',
                            justifyContent: "center",
                            alignItems: 'center'


                        }}
                        title="Log out"

                    ><img src={url}
                        onMouseOver={() => handleHover()}
                        onMouseOut={() => handleHover()}
                        style={{
                            width: '25px',
                            height: '25px',
                            borderRadius: '50%'
                        }} />
                    </div>
                </a>
            </Dropdown>
        </div>
    )
}

export default User;
