import { USER_LOGIN, USER_LOGOUT} from '../actions/types';

const initialState = {
  user: {},
  item: {},
  message:''
};

export default function(state = initialState, action) {
  switch (action.type) {
    case USER_LOGIN:
      return {
        ...state,
        user: action.payload,
        message:'You have fetched posts..'

      };
      case USER_LOGOUT:
        return {
          ...state,
          user: action.payload,
          message:'You have deleted a post.'
        };
    // case NEW_POST:
    //   return {
    //     ...state,
    //     item: action.payload,
    //     message:'You have added a post.'
    //   };
    default:
      return state;
  }
}
