import React, { useRef, useState, useEffect } from 'react';

const ImageView  = ({src,offShow,log,id}) =>{

    const [comments, setComment] = useState([])
    let index = 0;
    let cmt = []
     cmt = [
        'Lorem Ipsum is simply dummy text of the printing and',
        'Lorem Ipsum is simply dummy text of the printing and',
        'Lorem Ipsum is simply dummy text of the printing and',
        'This is greate',
        'Lorem Ipsum is simply dummy text of the printing and dummy, Lorem Ipsum is simply dummy text of the printing and dummy',
        'Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry'
    ]
    const handleClick = () =>{
        console.log(index);
        cmt.push('lorem is the best of devs')
        setComment(cmt)
        index++;
    }
    const ts = () =>{
        console.log("hihi");
    }
    useEffect(()=>{
        setComment(cmt)
    },[])
    const styleDiv = {
        width:'100%',
        height:'100%',
        zIndex:'100',
        backgroundColor: 'rgb(0,0,0)', /* Fallback color */
        backgroundColor: 'rgba(0,0,0,0.4)', /* Black w/ opacity */
        position:'relative',

    }
    return (
        <div style={styleDiv} >
           <div style={{
               width:'400px',
               height:'370px',
               position:'absolute',
               marginLeft:'30%',
               marginTop:'100px',
               transform:'translate(-50%,0)',
               opacity:'1',

           }}>
                <img src={src} width={700} height={500}  />
           </div>
           <div
            style={{
                width:'350px',
                maxHeight:'700px',
                position:'absolute',
                marginLeft:'65%',
                marginTop:'100px',
                // transform:'translate(-50%,0)',
                opacity:'1',
                borderRadius : '4px',
                backgroundColor:'#FFF',
                boxShadow:'0 1px 2px rgba(0,0,0,.1)',
                padding:'15px 20px',
                zIndex:'1000'

            }}>
                <di style={{
                    position:'absolute',
                    right:'15px',
                    top:'0px',
                    fontSize:'18px',
                    padding:'5px 5px',
                    cursor:'pointer'
                }}
                onClick={()=>offShow(false)}
                >
                    x
                </di>
                <h3>{log.title.toUpperCase()}</h3>
                <p
                    style={{
                        marginTop:'0',
                        marginBottom:'0',
                        marginLeft:'50%',
                        fontWeight:'bold'
                    }}
                >
                   Author: {log.author}
                </p>
                <p
                    style={{
                       margin:'0'
                    }}
                >
                   Comments(0)
                </p>
                <div className="comments" 
                    style={{
                        marginLeft:'15px',
                        height:'400px',
                        overflow:'scroll'
                    }}  
                >
                    {
                        comments.map((cmt,index) =>(
                            <React.Fragment key={index}>
                            <p className="cmt-row"><strong>nguyenvnhai </strong>{cmt}</p>
                            </React.Fragment>
                        ))
                    }
                    <button
                        onClick={()=>handleClick()}
                    >
                        Add more
                    </button>
                    {/* <p className="cmt-row"><strong>nguyenvnhai </strong>This is so greate</p>
                    <p className="cmt-row"><strong>nguyenminhhieu </strong>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy</p>
                    <p className="cmt-row"><strong>nguyenthanhien </strong>Lorem Ipsum is simply dummy text of the printing and typesetting industry.</p>
                    <p className="cmt-row"><strong>nguyensin </strong>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy</p> */}
                </div>
           </div>
        </div>
    )
}

export default ImageView;
