import React, { Component } from 'react';

const ImageView  = ({src,offShow,value}) =>{
    const styleDiv = {
        width:'100%',
        height:'100%',
        zIndex:'100',
        backgroundColor:'black',
        opacity:'0.7'
    }
    return (
        <>
            <div style={styleDiv}> 
            </div>
            <div style={{
                width:'400px',
                height:'370px',
                opacity:'1',
                zIndex:'101'
            }}>
                <img src={src} width={600} height={450} onClick={()=>offShow(false)} />
            </div>
        </>
    )
}

export default ImageView;
