import React, { useState} from 'react';

const AddBox = ({reload}) =>{
   // console.log(lng);
    const [title, setTitle] = useState("");
    const [latitude, setlatitude] = useState("");
    const [longitude, setlongitude] = useState("");
    const [comments, setcomments] = useState("");
    const [author, setauthor] = useState("");
    const [visitDate, setvisitDate] = useState("");
    const [image, setimage] = useState("");

    const style = {
        position:'absolute',
        width:'330px',
        height:'330px',
        margin:'50px 50px',
        backgroundColor:'#FFF',
        boxShadow:'0 1px 2px rgba(0,0,0,.1)',
        borderRadius:'3px',
        textAlign:'center',
        paddingTop:'30px'
    }
    const handleChange = (e) =>{
        //setValue(e.target.value)
    }
    const handleAdd = () =>{
        fetch('http://localhost:3000/addlogs',{
            method: 'post',
            headers:{
                'Content-Type': 'application/json'
            },
            body:JSON.stringify({
                "title":title,
                "latitude":latitude,
                "longitude":longitude,
                "comments" :comments,
                "author":author,
                "visitDate":visitDate,
                "image":image
             })
        })
        .then(res => res.json())
        .then(result =>{
            if(result.done){
                reload();
            }else{
                alert("An error occured!")
            }
        })
    }
    return (
        <div style= {style}>
            <input type="text" placeholder="enter title" style={{
                                width:'90%',
                                height:'25px',
                                marginBottom:'10px'
                            }} onClick ={(e)=>setTitle(e.target.value)}  />
            <input type="text" placeholder="enter latitude" style={{
                                width:'40%',
                                height:'25px',
                                marginBottom:'10px',
                                marginRight:'20px'
                            }} onClick ={(e)=>setlatitude(e.target.value)} />
            <input type="text" placeholder="enter longtiude" style={{
                                width:'40%',
                                height:'25px',
                                marginBottom:'10px'
                            }} onClick ={(e)=>setlongitude(e.target.value) }/>
            <input type="text" placeholder="enter comments" style={{
                                width:'90%',
                                height:'25px',
                                marginBottom:'10px'
                            }} onClick ={(e)=>setcomments(e.target.value) }/>
            <input type="text" placeholder="enter author" style={{
                                width:'90%',
                                height:'25px',
                                marginBottom:'10px'
                            }} onClick ={(e)=>setauthor(e.target.value)} />
            <input type="text" placeholder="enter visitdate" style={{
                                width:'90%',
                                height:'25px',
                                marginBottom:'10px'
                            }} onClick ={(e)=>setvisitDate(e.target.value)} />
            <input type="text" placeholder="enter image" style={{
                                width:'90%',
                                height:'25px',
                                marginBottom:'10px'
                            }} onClick ={(e)=>setimage(e.target.value) }/>

            <button type="submit" 
            style={{
                padding:'5px 50px',
                marginTop:'5px',
                backgroundColor:'white',
                border:'1px solid black',
                borderRadius:'2px'
                }} 
            onClick={()=>handleAdd()}

            >Mark it</button>

        </div>
    )
}
export default AddBox;
